    function getCurrLocking()
        public
		view
        returns (uint256)
	{
	    if (lockupDate >= now)
	        return initLockupAmt;
	    
        uint256 diff = (now - lockupDate) / 2592000; // month diff
        uint256 partition = 10;
    
        if (diff >= partition) 
            return 0;
        else
            return initLockupAmt.mul(partition-diff).div(partition);
    }
}

