function computeSum(uint256 a, uint256 b) public pure returns(uint256) {
    uint256 _sumA = a.mul(a.add(1)).div(2);
    uint256 _sumB = b.mul(b.add(1)).div(2);
    return _sumB.sub(_sumA);
}

