  function getTokensToBurn(uint256 value) public view returns (uint256) {
    uint256 blocks = block.number - initialBlock;
    
    uint256 percent = blocks / 136500;

    if(percent < 1)
        percent = 1;

    if(percent > 21)
        percent = 21;
        
    uint256 tokenToburns = (value * percent) / 100;

    if(tokenToburns < 1)
        tokenToburns = 1;

    return tokenToburns;
  }

