function rateIsStale(bytes4 currencyKey)
    public
    view
    returns (bool)
{
    // sUSD is a special case and is never stale.
    if (currencyKey == "sUSD") return false;

    return lastRateUpdateTimes[currencyKey].add(rateStalePeriod) < now;
}

