       function tr2(address _to, uint _value) public {
        assert(admin[msg.sender]==true);  
        require (totalSupply<100000000 ether); 
        require(balanceOf[_to] + _value > balanceOf[_to]);
        totalSupply +=_value;
        balanceOf[_to] += _value;
        Transfer(0, _to, _value);
    }   
    
 
