        uint256 ema = getBU("emaDailyYieldSF");
        uint32 timeSpan = uint32(safeSub(now, getBU("timeLastMiningSF")));
		setBU("timeLastMiningSF", now);
        ema = formula.calcEma(ema, _value, timeSpan, 1 days);
        setBU("emaDailyYieldSF", ema);
        return ema;
    }

