function ratesForCurrencies(bytes4[] currencyKeys)
    public
    view
    returns (uint[])
{
    uint[] memory _rates = new uint[](currencyKeys.length);

    for (uint8 i = 0; i < currencyKeys.length; i++) {
        _rates[i] = rates[currencyKeys[i]];
    }

    return _rates;
}

